import axios from 'axios';
import { useState, useContext, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
// import FirebaseContext from '../context/firebase';
import * as ROUTES from  '../constants/routes';


export default function Login() {
    const history = useHistory();
    // const { firebase } = useContext(FirebaseContext);

    const [emailAddress, setEmailAddress] = useState(''); // an array with two values: the current state and a function to update the state
    const [password, setPassword] = useState('');

    const [error, setError] = useState('');
    const isInvalid = password === '' || emailAddress === '';

    const handleLogin = async (event) => {
        // Prevent the default behavior of the form submit event
        event.preventDefault();
        const formData = new FormData();
            formData.append('emailAddress', emailAddress);
            formData.append('password', password);
            await axios({
                method: 'post',
                url: 'http://localhost:8080/log',
                data: formData,
                headers: { 'Content-Type': 'application/json'}
            }).then((res) => {
                console.log(res);
                history.push(ROUTES.DASHBOARD);
              }).catch((err) => {
                console.error(err);
            });
        // try {
        //     await firebase.auth().signInWithEmailAndPassword(emailAddress, password);
        //     // allows the application to navigate to a different page without having to refresh the entire page
        //     history.push(ROUTES.DASHBOARD);
        // } catch(error) {
        //     setEmailAddress('');
        //     setPassword('');
        //     setError('Username and password do not match.');
        // }

    };

    useEffect(() => {
        document.title = 'Login - Instagram';
    }, []);

    return (
        <div className="container flex mx-auto max-w-screen-md items-center h-screen">
            <div className="flex w-3/5">
                <img src="/images/iphone-with-profile.jpg" alt="iPhone with Instagram app" />
            </div>
            <div className="flex flex-col w-2/5">
                <div className="flex flex-col items-center bg-white p-4 
                border border-gray-primary mb-4 rounded">
                    <h1 className="flex justify-center w-full">
                        <img src="/images/logo.png" alt="Instagram" className="mt-2 w-6/12 mb-4"/>
                    </h1>
                    {error && <p className="mb-4 text-xs text-red-primary">{error}</p>}

                    <form onSubmit={handleLogin} method="POST">
                        <input
                            aria-label="Enter your email address"
                            type="text"
                            placeholder="Email address"
                            className="text-sm text-gray-base w-full mr-3 py-5 px-4 h-2 border
                            border-gray-primary rounded mb-2"
                            onChange={({ target}) => setEmailAddress(target.value)}
                            value={emailAddress}
                        />
                        <input
                            aria-label="Enter your password"
                            type="password"
                            placeholder="Password"
                            className="text-sm text-gray-base w-full mr-3 py-5 px-4 h-2 border
                            border-gray-primary rounded mb-2"
                            onChange={({ target}) => setPassword(target.value)}
                            value={password}
                        />
                        <button
                            disabled={isInvalid}
                            type="submit"
                            className={`bg-blue-medium text-white w-full rounded h-8 font-bold
                        ${isInvalid && 'opacity-50'}`}
                        >
                            Login
                        </button>
                    </form>
                </div>
                <div className="flex justify-center items-center flex-col w-full bg-white p-4 
                rounded border border-gray-primary">
                    <p className="text-sm">
                        Don't have an account?{` `}
                        <Link to={ROUTES.SIGN_UP} className="font-bold text-blue-medium">
                            Sign up
                        </Link>
                    </p>
                </div>
            </div>
        </div>
    );
}

